import { problemsClient } from '@dynatrace-sdk/client-classic-environment-v2';

async function getProblemsList() {
  const response = await problemsClient.getProblems({
   from: 'now-7d', entitySelector: "type(APPLICATION)"
    //from: 'now-1d', problemSelector: "status(Closed)"
    //from: 'now-1d', problemSelector: "status(Open)"
  });
  const result = await response;
  // console.log(result.problems);
  return result.problems;
}

function expandArray(array) {
  if (array.length == 1) {
    return array[0].name;
  } else if (array.length > 1) {
    var retVal = ""
    for (let i=0;i<array.length;i++) {
      if (i!=0) {
        retVal = retVal + ", " + array[i].name;
      } else {
        retVal = array[i].name;
      }
    }
    return retVal;
  }
  return ""; 
}

export default async function () {
  const response = await getProblemsList();
  //console.log(response);
  const problems=response;
  const types = [{
      mappings: {
        startTime: {type: 'timestamp'},
        status: {type: 'string'},
        displayId: {type: 'string'},
        title: {type: 'string'},
        impactLevel: {type: 'string'},
        severityLevel: {type: 'string'},
        affectedEntities: {type: 'string'},
        impactedEntities: {type: 'string'},
        rootCauseEntity: {type: 'string'},
        duration: {type: 'double'},
        "event.id": {type: 'string'},
        endTime: {type: 'timestamp'}
        },
        indexRange: [
        0,
        problems.length
      ]
  }]
  //console.log(types);
  // Create a new empty data frame for the result
  const dataFrame = {
    records: [],
    types: types
  }

  for(let i=0; i<problems.length; i++) {
      const problem = problems[i];
      var duration = "";
      if (problem.endTime == null || problem.endTime == "" || problem.endTime < 0) {
        duration = Math.round(((Date.now() - problem.startTime)/1000)/60);
      } else {
        duration = Math.round(((problem.endTime-problem.startTime)/1000)/60);
      }
      var pstatus = "";
      if (problem.status == 'OPEN') {
        pstatus = "üî¥ OPEN";
      } else {
        pstatus = 'üü¢ CLOSED';
      }
      var affectedEntities = expandArray(problem.affectedEntities);
      var impactedEntities = expandArray(problem.impactedEntities);
      var rootCause = "";
      if (problem.rootCauseEntity == null) {
        rootCause = ""
      } else {
        rootCause = problem.rootCauseEntity["name"];
      }
       const record = {
              startTime: problem.startTime,
              status: pstatus,
              displayId: problem.displayId,
              title: problem.title,
              impactLevel: problem.impactLevel,
              severityLevel: problem.severityLevel,
              affectedEntities: affectedEntities,
              impactedEntities: impactedEntities,
              rootCauseEntity: rootCause,
              duration: duration,
              "event.id": problem.problemId,
              endTime: problem.endTime
            }
        dataFrame.records.push(record);
    }
  //console.log(dataFrame);
  return dataFrame;
}
